package String;

public class StringConcatenation {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String fname = "Nitesh";
		String lname = "Sigaram";
		System.out.println(fname +" "+lname);
		
		int a = 5;
		int b = 4;
		System.out.println("The Sum of "+a+" and "+b+ " is " +(a+b));
	}

}
