package flowcontrolstatements;

public class Switch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String fooditem;
		fooditem = "noodles";
		switch ("fooditem5") {
		case "fooditem":
			System.out.println("noodles");
			break;
		case "fooditem1":
			System.out.println("burger");
			break;
		case "fooditem3":
			System.out.println("fries");
			break;
		default:
			System.out.println("no food");
			break;
		}

	}
}
